css:
.fade-box {
  width: 200px;
  height: 100px;
  background-color: #f06;
  color: white;
  display: flex;
  justify-content: center;
  align-items: center;
  font-family: sans-serif;
  cursor: pointer;
  opacity: 0.7;
  transition: opacity .5s ease, transform .3s ease;
}

.fade-box:hover {
  opacity: 1;
  transform: scale(1.1);
}

.pulse-box {
  width: 250px;
  height: 150px;
  display: flex;
  justify-content: center;
  align-items: center;
  font-family: sans-serif;
  color: white;
  background-color: #f06;
  border-radius: 12px;
  animation: pulse 2s infinite alternate;
}

@keyframes pulse {
  0% { background-color: #f06; }
  50% { background-color: #f39; }
  100% { background-color: #f06; }
}

.glow-text {
  font-family: sans-serif;
  font-size: 48px;
  color: #fff;
  text-align: center;
  text-shadow: 0 0 5px #f06, 0 0 10px #f06, 0 0 20px #f39;
  transition: text-shadow .3s ease;
}

.glow-text:hover {
  text-shadow: 0 0 10px #f39, 0 0 20px #f06, 0 0 30px #f0f;
}

.pressed-btn {
  padding: 15px 30px;
  font-size: 16px;
  background-color: #06f;
  color: white;
  border: none;
  border-radius: 8px;
  cursor: pointer;
  box-shadow: 0 5px #0044aa;
  transition: all .2s ease;
}

.pressed-btn:active {
  transform: translateY(5px);
  box-shadow: 0 2px #0044aa;
}

.scene {
  display: flex;
  justify-content: center;
  align-items: center;
  perspective: 800px;
}

.card {
  width: 200px;
  height: 120px;
  background-color: #f06;
  color: white;
  display: flex;
  justify-content: center;
  align-items: center;
  border-radius: 12px;
  box-shadow: 0 10px 20px rgba(0,0,0,.4);
  transition: transform .4s ease, box-shadow .4s ease;
  transform-style: preserve-3d;
}

.card:hover {
  transform: translateZ(30px) rotateX(10deg) rotateY(10deg) scale(1.05);
  box-shadow: 0 20px 30px rgba(0,0,0,.6);
}

.gallery-wrap {
  position: relative;
  width: 100%;
  height: 100%;
}

#particle-canvas {
  position: absolute;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
}

