css:
.glitch {
  position: relative;
  font-size: 60px;
  font-weight: bold;
  color: #fff;
  font-family: 'Courier New', Courier, monospace;
}

.glitch::before,
.glitch::after {
  content: attr(data-text);
  position: absolute;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  background: #0f1720;
  overflow: hidden;
}

.glitch::before {
  left: 2px;
  text-shadow: -1px 0 red;
  animation: glitch-anim 2s infinite linear alternate-reverse;
}

.glitch::after {
  left: -2px;
  text-shadow: -1px 0 blue;
  animation: glitch-anim-2 2s infinite linear alternate-reverse;
}

@keyframes glitch-anim {
  0% { clip-path: inset(45% 0 50% 0); }
  20% { clip-path: inset(50% 0 20% 0); }
  40% { clip-path: inset(20% 0 70% 0); }
  60% { clip-path: inset(70% 0 10% 0); }
  80% { clip-path: inset(10% 0 40% 0); }
  100% { clip-path: inset(45% 0 50% 0); }
}

@keyframes glitch-anim-2 {
  0% { clip-path: inset(2% 0 90% 0); }
  20% { clip-path: inset(90% 0 5% 0); }
  40% { clip-path: inset(40% 0 45% 0); }
  60% { clip-path: inset(80% 0 10% 0); }
  80% { clip-path: inset(5% 0 70% 0); }
  100% { clip-path: inset(2% 0 90% 0); }
}
