  background: #232946;
  border-radius: 22px;
  display: flex;
  align-items: center;
  justify-content: center;
  transition: width 0.4s cubic-bezier(.22,1,.36,1);
  box-shadow: 0 4px 24px rgba(0,0,0,0.18);
}
.search-box.active {
  width: 240px;
}
.search-input {
  box-sizing: border-box;
  position: absolute;
  left: 0;
  top: 0;
  width: 100%;
  height: 100%;
  border: none;
  outline: none;
  background: transparent;
  color: #fff;
  font-size: 1rem;
  padding: 0 50px 0 20px;
  opacity: 0;
  pointer-events: none;
  transition: opacity 0.3s ease;
}
.search-box.active .search-input {
  opacity: 1;
