}
.search-box.active .search-btn {
  color: var(--secondary-accent, #e975a8);
}
.search-btn svg {
  display: block;
}

js:
function initExpandingSearchBar(container) {
  const searchBox = container.querySelector('.search-box');
  const searchBtn = container.querySelector('.search-btn');
  const searchInput = container.querySelector('.search-input');
  if (!searchBox || !searchBtn || !searchInput) return;

  const toggleSearch = (e) => {
    e.stopPropagation();
    searchBox.classList.toggle('active');
    if (searchBox.classList.contains('active')) {
      searchInput.focus();
      clearTimeout(container._searchCloseTimer);
    }
  };

  const startDelayedClose = () => {
    clearTimeout(container._searchCloseTimer);
    if (searchBox.classList.contains('active')) {
      container._searchCloseTimer = setTimeout(() => {
        searchBox.classList.remove('active');
      }, 750);
    }
  };

  const cancelDelayedClose = () => {
    clearTimeout(container._searchCloseTimer);
  };

  searchBtn.addEventListener('click', toggleSearch);
  container.addEventListener('mouseleave', startDelayedClose);
  container.addEventListener('mouseenter', cancelDelayedClose);

  return () => {
    searchBtn.removeEventListener('click', toggleSearch);
    container.removeEventListener('mouseleave', startDelayedClose);
    container.removeEventListener('mouseenter', cancelDelayedClose);
  };
}
window.initExpandingSearchBar = initExpandingSearchBar;

