.futuristic-toggle-container { font-size: 17px; }
.futuristic-toggle-container .switch { position: relative; display: inline-block; width: 3.5em; height: 2em; }
.futuristic-toggle-container .switch input { opacity: 0; width: 0; height: 0; }
.futuristic-toggle-container .slider { position: absolute; cursor: pointer; top: 0; left: 0; right: 0; bottom: 0; background-color: var(--surface-color); border: 1px solid var(--border-color); box-shadow: inset 0 2px 8px rgba(0,0,0,0.4); transition: .4s; border-radius: 5px; }
.futuristic-toggle-container .slider:before { position: absolute; content: ''; height: 1.4em; width: 0.2em; border-radius: 2px; left: 0.3em; bottom: 0.3em; background-color: #fff; box-shadow: 0 0 8px rgba(255,255,255,0.7); transition: .4s; }
.futuristic-toggle-container input:checked + .slider { background-color: var(--primary-accent); box-shadow: 0 0 15px var(--glow-color), inset 0 2px 8px rgba(0,0,0,0.4); }
