.profile-tooltip-wrapper { --tiktok-teal: #00f2ea; --tiktok-pink: #ff0050; display: flex; align-items: flex-end; justify-content: center; height: 100%; padding-bottom: 2rem; }
.profile-tooltip-wrapper .tooltip-container { position: relative; cursor: pointer; transition: all 0.2s; font-size: 17px; border-radius: 10px; }
.profile-tooltip-wrapper .tooltip { position: absolute; top: 0; left: 50%; transform: translateX(-50%); padding: 10px; opacity: 0; pointer-events: none; transition: all 0.3s; border-radius: 15px; box-shadow: inset 5px 5px 5px rgba(0, 0, 0, 0.2), inset -5px -5px 15px rgba(255, 255, 255, 0.1), 5px 5px 15px rgba(0, 0, 0, 0.3), -5px -5px 15px rgba(255, 255, 255, 0.1); }
.profile-tooltip-wrapper .profile { background: var(--surface-color); border-radius: 10px 15px; padding: 10px; border: 1px solid var(--border-color); }
