.profile-tooltip-wrapper .layer span, .profile-tooltip-wrapper .text { color: var(--tiktok-teal); border-color: var(--tiktok-teal); }
.active-preview .profile-tooltip-wrapper .icon:hover .layer span { box-shadow: -1px 1px 3px var(--tiktok-teal), 1px -1px 3px var(--tiktok-pink); }
.profile-tooltip-wrapper .icon .text { position: absolute; left: 50%; bottom: -5px; opacity: 0; font-weight: 500; transform: translateX(-50%); transition: bottom 0.3s ease, opacity 0.3s ease; }
