.futuristic-login-form-wrapper { display: flex; align-items: center; justify-content: center; width: 100%; height: 100%; }
.futuristic-login-form-wrapper .form { display: flex; flex-direction: column; gap: 10px; padding: 0.4em 2em 0.4em 2em; background-color: var(--bg-color); border-radius: 25px; transition: .4s ease-in-out; }
.futuristic-login-form-wrapper .card { background-image: linear-gradient(163deg, var(--secondary-accent) 0%, var(--primary-accent) 100%); border-radius: 22px; transition: all .3s; }
.futuristic-login-form-wrapper .card2 { border-radius: 0; transition: all .2s; }
.active-preview .futuristic-login-form-wrapper .card2:hover { transform: scale(0.98); border-radius: 20px; }
