.futuristic-login-form-wrapper .field { display: flex; align-items: center; justify-content: center; gap: 0.5em; border-radius: 25px; padding: 0.6em; border: none; outline: none; color: var(--text-color); background-color: var(--bg-color); box-shadow: inset 2px 5px 10px rgba(0,0,0,0.5); }
