.neumorphic-card-wrapper .icon { width: 60px; height: 60px; color: var(--primary-accent); background-color: var(--bg-color); margin: 20px auto; border-radius: 50%; box-shadow: 8px 8px 10px rgba(0,0,0,0.5), -8px -8px 10px rgba(255,255,255,0.05); display: flex; align-items: center; justify-content: center; }
