.animated-glow-card-wrapper .card::before { content: ''; position: absolute; inset: 0; left: -5px; margin: auto; width: 200px; height: 264px; border-radius: 10px; background: linear-gradient(-45deg, var(--secondary-accent) 0%, var(--primary-accent) 100% ); z-index: -10; pointer-events: none; transition: all 0.6s cubic-bezier(0.175, 0.885, 0.32, 1.275); }
.animated-glow-card-wrapper .card::after { content: ''; z-index: -1; position: absolute; inset: 0; background: linear-gradient(-45deg, var(--secondary-accent) 0%, var(--primary-accent) 100% ); transform: translate3d(0, 0, 0) scale(0.95); filter: blur(20px); }
.animated-glow-card-wrapper .heading { font-size: 20px; text-transform: capitalize; font-weight: 700; font-family: var(--font-display); color: var(--text-color); }
