function initLikeDislikeWidget(container) {
  const likeCheckbox = container.querySelector('#like-checkbox');
  const dislikeCheckbox = container.querySelector('#dislike-checkbox');
  if (!likeCheckbox || !dislikeCheckbox) return;

  const handleLikeChange = () => {
    if (likeCheckbox.checked) {
      dislikeCheckbox.checked = false;
    }
  };

  const handleDislikeChange = () => {
    if (dislikeCheckbox.checked) {
      likeCheckbox.checked = false;
    }
  };

  likeCheckbox.addEventListener('change', handleLikeChange);
  dislikeCheckbox.addEventListener('change', handleDislikeChange);

  // Return a cleanup function
  return () => {
    likeCheckbox.removeEventListener('change', handleLikeChange);
    dislikeCheckbox.removeEventListener('change', handleDislikeChange);
  };
}
window.initLikeDislikeWidget = initLikeDislikeWidget;

