.flipping-glow-card-wrapper { display: flex; align-items: center; justify-content: center; width: 100%; height: 100%; perspective: 1000px; }
.flipping-glow-card-wrapper .card { overflow: visible; width: 190px; height: 254px; }
.flipping-glow-card-wrapper .content { width: 100%; height: 100%; transform-style: preserve-3d; transition: transform 300ms; box-shadow: 0px 0px 10px 1px #000000ee; border-radius: 5px; }
.flipping-glow-card-wrapper .front, .flipping-glow-card-wrapper .back { background-color: var(--surface-color); position: absolute; width: 100%; height: 100%; backface-visibility: hidden; -webkit-backface-visibility: hidden; border-radius: 5px; overflow: hidden; }
.flipping-glow-card-wrapper .back { width: 100%; height: 100%; justify-content: center; display: flex; align-items: center; overflow: hidden; }
.flipping-glow-card-wrapper .back::before { position: absolute; content: ' '; display: block; width: 160px; height: 160%; background: linear-gradient(90deg, transparent, var(--primary-accent), var(--secondary-accent), var(--primary-accent), transparent); animation: fgc-rotation 5s infinite linear; }
.flipping-glow-card-wrapper .back-content { position: absolute; width: 99%; height: 99%; background-color: var(--surface-color); border-radius: 5px; color: white; display: flex; flex-direction: column; justify-content: center; align-items: center; gap: 30px; }
.active-preview .flipping-glow-card-wrapper .card:hover .content { transform: rotateY(180deg); }
@keyframes fgc-rotation { 0% { transform: rotateZ(0deg); } 100% { transform: rotateZ(360deg); } }
.flipping-glow-card-wrapper .front { transform: rotateY(180deg); color: white; }
.flipping-glow-card-wrapper .front .front-content { position: absolute; width: 100%; height: 100%; padding: 10px; display: flex; flex-direction: column; justify-content: space-between; }
.flipping-glow-card-wrapper .front-content .badge { background-color: rgba(0,0,0,0.5); padding: 2px 10px; border-radius: 10px; backdrop-filter: blur(2px); width: fit-content; }
.flipping-glow-card-wrapper .description { box-shadow: 0px 0px 10px 5px rgba(0,0,0,0.5); width: 100%; padding: 10px; background-color: rgba(0,0,0,0.6); backdrop-filter: blur(5px); border-radius: 5px; }
.flipping-glow-card-wrapper .title { font-size: 11px; max-width: 100%; display: flex; justify-content: space-between; }
.flipping-glow-card-wrapper .title p { width: 50%; }
.flipping-glow-card-wrapper .card-footer { color: #ffffff88; margin-top: 5px; font-size: 8px; }
.flipping-glow-card-wrapper .front .img { position: absolute; width: 100%; height: 100%; object-fit: cover; object-position: center; }
.flipping-glow-card-wrapper .circle { width: 90px; height: 90px; border-radius: 50%; background-color: var(--primary-accent); position: relative; filter: blur(15px); animation: fgc-floating 2600ms infinite linear; }
.flipping-glow-card-wrapper #bottom { background-color: var(--secondary-accent); left: 50px; top: 0px; width: 150px; height: 150px; animation-delay: -800ms; }
.flipping-glow-card-wrapper #right { background-color: #ff2233; left: 160px; top: -80px; width: 30px; height: 30px; animation-delay: -1800ms; }
@keyframes fgc-floating { 0% { transform: translateY(0px); } 50% { transform: translateY(10px); } 100% { transform: translateY(0px); } }

