.cyberpunk-button-wrapper { display: flex; align-items: center; justify-content: center; width: 100%; height: 100%; }
.cyberpunk-button {
  font-family: var(--font-display), sans-serif;
  color: var(--primary-accent);
  cursor: pointer;
  font-size: 1.5rem;
  padding: 1rem 2rem;
  border: 1px solid var(--primary-accent);
  background: transparent;
  position: relative;
  overflow: hidden;
  transition: all 0.3s;
  box-shadow: 0 0 5px var(--primary-accent), inset 0 0 5px var(--primary-accent);
  text-shadow: 0 0 3px var(--primary-accent);
}
.cyberpunk-button::before {
  content: '';
  position: absolute;
  top: 4px;
  left: 4px;
  width: 10px;
  height: 10px;
  border-top: 2px solid var(--secondary-accent);
  border-left: 2px solid var(--secondary-accent);
  transition: all 0.3s;
}
.cyberpunk-button::after {
  content: '';
  position: absolute;
  bottom: 4px;
  right: 4px;
  width: 10px;
  height: 10px;
  border-bottom: 2px solid var(--secondary-accent);
  border-right: 2px solid var(--secondary-accent);
  transition: all 0.3s;
}
.cyberpunk-button > span {
  position: relative;
  z-index: 2;
}
.cyberpunk-button .scanline {
  position: absolute;
  top: -10px; /* Set initial position to match animation start */
  left: 0;
  width: 100%;
  height: 3px;
  background: var(--secondary-accent);
  box-shadow: 0 0 10px var(--secondary-accent);
  opacity: 0;
}
@keyframes scanline-move {
  from { top: -10px; }
  to { top: 100%; }
}
@keyframes scanline-fade {
  0% { opacity: 1; }
  90% { opacity: 1; }
  100% { opacity: 0; }
}
.active-preview .cyberpunk-button:hover {
  color: var(--secondary-accent);
  border-color: var(--secondary-accent);
  box-shadow: 0 0 20px var(--secondary-accent), inset 0 0 10px var(--secondary-accent);
  text-shadow: 0 0 10px var(--secondary-accent);
}
.active-preview .cyberpunk-button:hover::before, .active-preview .cyberpunk-button:hover::after {
  width: calc(100% - 8px);
  height: calc(100% - 8px);
}
.active-preview .cyberpunk-button:hover .scanline {
  animation: scanline-move 1.5s linear forwards, scanline-fade 1.5s linear forwards;
  animation-delay: 0.2s;
}

.liquid-switch-wrapper {
  display: flex;
  align-items: center;
  justify-content: center;
  width: 100%;
  height: 100%;
  filter: url(#liquid-goo);
}

.liquid-switch {
  position: relative;
  display: inline-block;
  width: 100px;
  height: 40px;
  cursor: pointer;
}

.liquid-switch input {
  opacity: 0;
  width: 0;
  height: 0;
}

.liquid-switch-track {
  position: absolute;
  top: 0;
  left: 0;
  right: 0;
  bottom: 0;
  background-color: var(--surface-color);
  border-radius: 20px;
  box-shadow: inset 0 2px 5px rgba(0,0,0,0.3);
}

.liquid-track-pinch {
  position: absolute;
  width: 8px;
  height: 20px;
  background-color: var(--bg-color);
  border-radius: 4px;
  top: 50%;
  left: 50%;
  transform: translate(-50%, -50%);
}

/* Static blobs at the ends */
.liquid-switch-track::before, .liquid-switch-track::after {
  content: '';
  position: absolute;
  width: 30px;
  height: 30px;
  border-radius: 50%;
  top: 5px;
}

.liquid-switch-track::before {
  left: 5px;
  background-color: var(--bg-color);
}

.liquid-switch-track::after {
  right: 5px;
  background-color: var(--bg-color);
}

.liquid-switch-handle {
  position: absolute;
  width: 30px;
  height: 30px;
  top: 5px;
  left: 5px;
  background-color: var(--secondary-accent);
  border-radius: 50%;
  transition: transform 0.4s cubic-bezier(0.76, 0, 0.24, 1);
  z-index: 1; /* Ensure handle is on top of the track */
}

.liquid-switch input:checked ~ .liquid-switch-handle {
  transform: translateX(60px);
  background-color: var(--primary-accent);
}

.gooey-blob-loader-inverted-wrapper { display: flex; align-items: center; justify-content: center; width: 100%; height: 100%; }
.gooey-blob-container-inverted { width: 200px; height: 200px; position: relative; margin: auto; filter: url("#gooey-blob-filter-inverted"); animation: gooey-blob-rotate-move-inverted 2s ease-in-out infinite paused; }
.gooey-blob-loader-inverted-wrapper .dot-inverted { width: 70px; height: 70px; border-radius: 50%; background-color: #000; position: absolute; top: 0; bottom: 0; left: 0; right: 0; margin: auto; }
.active-preview .gooey-blob-container-inverted, .active-preview .gooey-blob-loader-inverted-wrapper .dot-inverted { animation-play-state: running; }
.gooey-blob-loader-inverted-wrapper .dot-1-inverted { background-color: var(--primary-accent); animation: gooey-blob-dot-1-move-inverted 2s ease infinite paused, gooey-blob-index-inverted 6s -2s ease infinite paused; }
.gooey-blob-loader-inverted-wrapper .dot-2-inverted { background-color: var(--secondary-accent); animation: gooey-blob-dot-2-move-inverted 2s ease infinite paused, gooey-blob-index-inverted 6s -4s ease infinite paused; }
.gooey-blob-loader-inverted-wrapper .dot-3-inverted { background-color: var(--text-color); animation: gooey-blob-dot-3-move-inverted 2s ease infinite paused, gooey-blob-index-inverted 6s ease infinite paused; }
@keyframes gooey-blob-dot-1-move-inverted { 20% { transform: scale(1); } 45% { transform: translate(16px, 12px) scale(0.45); } 60% { transform: translate(80px, 60px) scale(0.45); } 80% { transform: translate(80px, 60px) scale(0.45); } 100% { transform: translateY(0px) scale(1); } }
@keyframes gooey-blob-dot-2-move-inverted { 20% { transform: scale(1); } 45% { transform: translate(-16px, 12px) scale(0.45); } 60% { transform: translate(-80px, 60px) scale(0.45); } 80% { transform: translate(-80px, 60px) scale(0.45); } 100% { transform: translateY(0px) scale(1); } }
@keyframes gooey-blob-dot-3-move-inverted { 20% { transform: scale(1); } 45% { transform: translateY(-18px) scale(0.45); } 60% { transform: translateY(-90px) scale(0.45); } 80% { transform: translateY(-90px) scale(0.45); } 100% { transform: translateY(0px) scale(1); } }
@keyframes gooey-blob-rotate-move-inverted {
  0%, 45% { transform: rotate(0deg); }
  80% { transform: rotate(360deg); }
  100% { transform: rotate(360deg); }
}
@keyframes gooey-blob-index-inverted { 0%, 100% { z-index: 3; } 33.3% { z-index: 2; } 66.6% { z-index: 1; } }
